    private void jtf_nombre_registroKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jtf_nombre_registroKeyTyped
        char c = evt.getKeyChar();
        
        if(c<'0'||c<'9')
            evt.consume();
    }//GEN-LAST:event_jtf_nombre_registroKeyTyped

