    private void jtf_edad_registroKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jtf_edad_registroKeyTyped
        char c = evt.getKeyChar();
        
        if(!(c<'0'||c<'9'))
            evt.consume();
    }//GEN-LAST:event_jtf_edad_registroKeyTyped

